// This is a minimal manual implementation of GameMatcher
// In production, this would be auto-generated by Entitas.CodeGeneration
using Entitas;

namespace U2.Shared.ECS;

public static class GameMatcher
{
    private static IMatcher<GameEntity>? _transform2D;
    private static IMatcher<GameEntity>? _velocity;
    private static IMatcher<GameEntity>? _momentum;
    private static IMatcher<GameEntity>? _mass;
    private static IMatcher<GameEntity>? _controlState;
    private static IMatcher<GameEntity>? _flightAssist;
    private static IMatcher<GameEntity>? _shipConfig;
    private static IMatcher<GameEntity>? _health;
    private static IMatcher<GameEntity>? _playerOwned;

    public static IMatcher<GameEntity> Transform2D => _transform2D ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.Transform2D);
    
    public static IMatcher<GameEntity> Velocity => _velocity ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.Velocity);
    
    public static IMatcher<GameEntity> Momentum => _momentum ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.Momentum);
    
    public static IMatcher<GameEntity> Mass => _mass ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.Mass);
    
    public static IMatcher<GameEntity> ControlState => _controlState ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.ControlState);
    
    public static IMatcher<GameEntity> FlightAssist => _flightAssist ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.FlightAssist);
    
    public static IMatcher<GameEntity> ShipConfig => _shipConfig ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.ShipConfig);
    
    public static IMatcher<GameEntity> Health => _health ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.Health);
    
    public static IMatcher<GameEntity> PlayerOwned => _playerOwned ??= 
        Matcher<GameEntity>.AllOf(GameComponentsLookup.PlayerOwned);

    public static IMatcher<GameEntity> AllOf(params int[] indices)
    {
        return Matcher<GameEntity>.AllOf(indices);
    }

    public static IMatcher<GameEntity> AnyOf(params int[] indices)
    {
        return Matcher<GameEntity>.AnyOf(indices);
    }

    public static IMatcher<GameEntity> AllOf(params IMatcher<GameEntity>[] matchers)
    {
        return Matcher<GameEntity>.AllOf(matchers);
    }

    public static IMatcher<GameEntity> AnyOf(params IMatcher<GameEntity>[] matchers)
    {
        return Matcher<GameEntity>.AnyOf(matchers);
    }
}
